import React from 'react'
import styled from 'styled-components'

const Job = (props) => {
   const {job, pay, name, phone, location, description, info} = props;
  return (
    <JobStyled>
      <div><span className='title'>Good Job: </span>{job}</div>
      <div><span>Pay: </span>{pay}</div>
      <div>
        <ul>
          <li><span>Name: </span>{name}</li>
          <li><span>Phone number: </span>{phone}</li>
      </ul>
      <div><span>Location: </span>{location}</div>
      <div><span>Description of Job: </span>{description}</div>
      <div><span>Additional info: </span>{info}</div>
      </div>
    </JobStyled>
  )
}

const JobStyled = styled.section`
  padding: 3rem;
  background-color: #3BB57533;
  margin: 1rem;
  border-radius: 1rem;
  max-width: 200px;
  .title {
    font-weight: bold;
  }
`

export default Job
